// each story has this post_message identifier
let arr = htmlBlock.split('<div data-testid="post_message" ');
// removing all code above the first story
arr.shift();
// removing the first 'story' since it is hony's pinned Patreon message
arr.shift();
let stories = [];
arr.forEach((str, idx) => {
  let story = { 
    idx, 
    content: '', 
    url: '', 
    sequence: false, 
    location: null, 
    length: null, 
    microfashion: false 
  };
  // long stories, have the following class
  if (str.includes('class="text_exposed_root"><p>')) {
    story.length = 'long';
    str = str.split('class="text_exposed_root"><p>')[1];
    str = str.split('<span class="text_exposed_hide">...</span><span class="text_exposed_show">').join('');
    if (str.includes(')</p></div><span class="text_exposed_hide">')) {
      // city data is available
      str = str.split(')</p></div><span class="text_exposed_hide">')[0];
      story.location = str.split('</span></p><div class="text_exposed_show"><p> (')[1];
      str = str.split('</span></p><div class="text_exposed_show"><p> (')[0];
    } else {
      // city data is not available
      str = str.split('</span></p><span class="text_exposed_hide"> ')[0];
    }
  } else {
    // this is not a long story (~ < 800 chars)
    story.length = 'short';
    str = str.split(' id="')[1].split('"><p>')[1];
    str = str.split('</p></div>')[0];
    if (str[str.length - 1] === ')') {
      // city data is available
      story.location = str.split('</p><p>')[1].trim().slice(1, -1);
      str = str.split('</p><p>')[0];
    }
  }
  if (str[0] === '(') story.sequence = true;
  if (str[0] === 'T') story.microfashion = true;
  story.content = str.trim();
  stories.push(story);
});

